INSERT INTO `categories_packages` VALUES (1,'seo title','','seo keywords','url','Special','<p>Peru For Less offers you fully customizable vacations to all the best places to visit in Peru. Each package is guaranteed to offer top-value tours, transfers, and accommodations. Pick your favorite Peru tour package and contact one of our&nbsp;<a href=\"http://www.peruforless.com/contactus.php\">Peru travel experts</a>&nbsp;to start designing your dream Peru vacation package today.</p>\r\n',1,0,'2014-09-04 18:33:53','2014-09-03 17:50:01',NULL),(2,'','','','','Tours','',1,0,'2014-09-04 17:37:11','2014-09-03 19:16:47',NULL),(3,'','','','','Amazon Tours','',0,0,'2014-09-04 17:18:47','2014-09-03 19:17:36',NULL),(4,NULL,NULL,NULL,NULL,'Deals',NULL,0,0,'2014-09-03 19:17:49','2014-09-03 19:17:49',NULL),(5,NULL,NULL,NULL,NULL,'amaxon tours xxx',NULL,0,0,'2014-09-03 21:24:14','2014-09-03 21:24:14',NULL),(6,'','','','heart-of-the-inca-cusco-machu-picchu-test1','Heart of the Inca - Cusco & Machu Picchu-test1','<p>Contenido</p>\r\n',0,0,'2014-09-03 22:52:41','2014-09-03 22:52:41',NULL),(7,'','','','category-form','category form','',0,0,'2014-09-04 17:18:36','2014-09-04 17:18:36',NULL),(8,'','','','category','category','',0,0,'2014-09-04 17:20:37','2014-09-04 17:20:37',NULL),(9,'','','','category-name-test-1','category name test 1','',0,0,'2014-09-04 17:33:49','2014-09-04 17:33:49',NULL);
